INSERT INTO `pages` (`id`, `name`, `html`, `css`, `js`, `theme`, `pageable_id`, `pageable_type`, `description`, `tags`, `title`, `libraries`, `created_at`, `updated_at`) VALUES
(10, 'index', '<!DOCTYPE html>\n<html lang="en">\n\n<head>\n\n    <meta charset="utf-8">\n    <meta http-equiv="X-UA-Compatible" content="IE=edge">\n    <meta name="viewport" content="width=device-width, initial-scale=1">\n    <meta name="description" content="">\n    <meta name="author" content="">\n\n    <title>Clean Blog</title>\n\n    <!-- Custom Fonts -->\n    <link class="include" href=\'http://fonts.googleapis.com/css?family=Lora:400,700,400italic,700italic\' rel=\'stylesheet\' type=\'text/css\'>\n    <link class="include" href=\'http://fonts.googleapis.com/css?family=Open+Sans:300italic,400italic,600italic,700italic,800italic,400,300,600,700,800\' rel=\'stylesheet\' type=\'text/css\'>\n\n</head>\n\n<body>\n\n    <!-- Navigation -->\n    <nav class="navbar navbar-default navbar-custom navbar-fixed-top">\n        <div class="container-fluid">\n            <!-- Brand and toggle get grouped for better mobile display -->\n            <div class="navbar-header page-scroll">\n                <button type="button" class="navbar-toggle" data-toggle="collapse" data-target="#bs-example-navbar-collapse-1">\n                    <span class="sr-only">Toggle navigation</span>\n                    <span class="icon-bar"></span>\n                    <span class="icon-bar"></span>\n                    <span class="icon-bar"></span>\n                </button>\n                <a class="navbar-brand" href="index.html">Start Bootstrap</a>\n            </div>\n\n            <!-- Collect the nav links, forms, and other content for toggling -->\n            <div class="collapse navbar-collapse" id="bs-example-navbar-collapse-1">\n                <ul class="nav navbar-nav navbar-right">\n                    <li>\n                        <a href="index.html">Home</a>\n                    </li>\n                    <li>\n                        <a href="about.html">About</a>\n                    </li>\n                    <li>\n                        <a href="post.html">Sample Post</a>\n                    </li>\n                    <li>\n                        <a href="contact.html">Contact</a>\n                    </li>\n                </ul>\n            </div>\n            <!-- /.navbar-collapse -->\n        </div>\n        <!-- /.container -->\n    </nav>\n\n    <!-- Page Header -->\n    <!-- Set your background image for this header on the line below. -->\n    <header class="intro-header" style="background-image: url(\'templates/clean-blog/images/home-bg.jpg\')">\n        <div class="container">\n            <div class="row">\n                <div class="col-lg-8 col-lg-offset-2 col-md-10 col-md-offset-1">\n                    <div class="site-heading">\n                        <h1>Clean Blog</h1>\n                        <hr class="small">\n                        <span class="subheading">A Clean Blog Theme by Start Bootstrap</span>\n                    </div>\n                </div>\n            </div>\n        </div>\n    </header>\n\n    <!-- Main Content -->\n    <div class="container">\n        <div class="row">\n            <div class="col-lg-8 col-lg-offset-2 col-md-10 col-md-offset-1">\n                <div class="post-preview">\n                    <a href="post.html">\n                        <h2 class="post-title">\n                            Man must explore, and this is exploration at its greatest\n                        </h2>\n                        <h3 class="post-subtitle">\n                            Problems look mighty small from 150 miles up\n                        </h3>\n                    </a>\n                    <p class="post-meta">Posted by <a href="#">Start Bootstrap</a> on September 24, 2014</p>\n                </div>\n                <hr>\n                <div class="post-preview">\n                    <a href="post.html">\n                        <h2 class="post-title">\n                            I believe every human has a finite number of heartbeats. I don\'t intend to waste any of mine.\n                        </h2>\n                    </a>\n                    <p class="post-meta">Posted by <a href="#">Start Bootstrap</a> on September 18, 2014</p>\n                </div>\n                <hr>\n                <div class="post-preview">\n                    <a href="post.html">\n                        <h2 class="post-title">\n                            Science has not yet mastered prophecy\n                        </h2>\n                        <h3 class="post-subtitle">\n                            We predict too much for the next year and yet far too little for the next ten.\n                        </h3>\n                    </a>\n                    <p class="post-meta">Posted by <a href="#">Start Bootstrap</a> on August 24, 2014</p>\n                </div>\n                <hr>\n                <div class="post-preview">\n                    <a href="post.html">\n                        <h2 class="post-title">\n                            Failure is not an option\n                        </h2>\n                        <h3 class="post-subtitle">\n                            Many say exploration is part of our destiny, but it’s actually our duty to future generations.\n                        </h3>\n                    </a>\n                    <p class="post-meta">Posted by <a href="#">Start Bootstrap</a> on July 8, 2014</p>\n                </div>\n                <hr>\n                <!-- Pager -->\n                <ul class="pager">\n                    <li class="next">\n                        <a href="#">Older Posts &rarr;</a>\n                    </li>\n                </ul>\n            </div>\n        </div>\n    </div>\n\n    <hr>\n\n    <!-- Footer -->\n    <footer>\n        <div class="container">\n            <div class="row">\n                <div class="col-lg-8 col-lg-offset-2 col-md-10 col-md-offset-1">\n                    <ul class="list-inline text-center">\n                        <li>\n                            <a href="#">